import React,{ useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { useDispatch, useSelector } from 'react-redux';
import {loginUser} from '../../Redux';
import {Redirect} from 'react-router-dom'
import { useHistory } from 'react-router-dom'
import { useForm } from "react-hook-form";
import _ from "lodash/fp";
import * as yup from "yup";

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const LoginValidation = yup.object().shape({
  email: yup.string().required(),
  password: yup.string().required()
});


const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function Login() {
  const { register, errors, handleSubmit } = useForm();
  const classes = useStyles();
  const authenticated= useSelector(state=>state.auth.authenticated)
  const dispatch = useDispatch();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const onSubmit=(e)=>{
    e.preventDefault();
    const details = {
        email, password
    }
  console.log(details)
    dispatch(loginUser(details));
  }
  const history = useHistory();
  const handleLogin=()=>
  {
    history.push(`/signup`);
  }
  if(authenticated){
    return(<div>
        <Redirect to="/" />
      </div>)
        
    
}
  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign in
        </Typography>
        <form className={classes.form} onSubmit={handleSubmit(onSubmit)} >
        <TextField
          variant="outlined"
          margin="normal"
          required
          fullWidth
          id="email"
          label="Email Address"
          placeholder="Enter Your Email ID"
          autoComplete="email"
          autoFocus
          name="firstName"
          ref={register({
            required: true,
            maxLength: 20,
            pattern: /^[A-Za-z]+$/i
          })}
        />
        {_.get("firstName.type", errors) === "required" && (<p>This field is required</p>)}
        {_.get("firstName.type", errors) === "maxLength" && (<p>First name cannot exceed 20 characters</p>)}
        {_.get("firstName.type", errors) === "pattern" && (<p>Alphabetical characters only</p>)}
        <TextField
        variant="outlined"
        margin="normal"
        required
        fullWidth
        placeholder="Enter Your Password"
        id="password"
        label="Password"
        autoComplete="password"
        autoFocus
        name="password" 
        ref={register({ pattern: /^[A-Za-z]+$/i })} 
        />
        {_.get("password.type", errors) === "pattern" && (<p>Alphabetical characters only</p>)}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
            onClick={onSubmit}
          >
            Sign In
          </Button>
          <Grid container>
            <Grid item xs>
              <Link href="#" variant="body2">
                Forgot password?
              </Link>
            </Grid>
            <Grid item>
            <Link href="#" variant="body2" onClick={handleLogin}>{"Don't have an account? Sign Up"}</Link>
                
            </Grid>
          </Grid>
        </form>
      </div>
      <Box mt={8}>
        <Copyright />
      </Box>
    </Container>
  );
}
