import { ADD_ADDRESS_FAILURE,ADD_ADDRESS_REQUEST,ADD_ADDRESS_SUCCESS  } from "./AddressType"
const initialState={
    loading:false,
    addressAdded:false,
    address:[],
    error:''
}
const reducer=(state=initialState,action)=>{
    switch(action.type){
        case ADD_ADDRESS_REQUEST:
           return{
                ...state,
                loading:true,
                addressAdded:false
            }
            case ADD_ADDRESS_SUCCESS:
                return{
                    loading:false,
                    addressAdded:true,
                    address:action.payload,
                    error:''
                }
                case ADD_ADDRESS_FAILURE:
                    return{
                        loading:false,
                        signedup:false,
                        address:[],
                        error:action.payload
                    }
                    default: return state
    }
}
export default reducer
