import React,{useState,useEffect} from 'react'
import axios from 'axios'
import Productcard from '../ProductsCard/ProductCard'
import {Container,Row,Col} from 'react-bootstrap'
import './Products.css'
import ProductCardSkeleton from '../ProductsCard/ProductCardSkeleton'
function Products(props) {
    const auth = useSelector((state) => state.auth);
    const dispatch = useDispatch();
    const url ='https://golocal999.herokuapp.com/api/getproduct'
    const [products,setProducts] =useState({
        loading:false,
        data:null,
        error:false
    })
    useEffect(() => {
        setProducts({
            loading:true,
            data:null,
            error:false
        })
    axios.get(url)
    .then(response=>{
        setProducts({
            loading:false,
            data:response.data.products,
            error:false
        })
    })
    .catch(()=>{
        setProducts({
            loading:false,
            data:null,
            error:true
        } 
    )
    })}, [url])
    let content =null
    const n = 10;
    if(products.error)
    {
        content=<p>Error fetching the data please check your internet connection</p>
    }
    if(products.loading)
    {
        content=[...Array(n)].map((e, i) =><div key={i}><Col className="each-product"><ProductCardSkeleton /></Col></div>
        )}
    if(products.data)
    {
        content=products.data.filter((product)=>{
            if(props.search===""){
                return product
            }else if(product.name.toLowerCase().includes(props.search.toLowerCase())){
                return product
            }
        }).map((product,key)=>
        <div key={key}>
        <Col className="each-product"><Productcard product={product}/></Col>
        
  </div>
        )
    }
    return (
        <div>
          <Container className="products-container" >
          <Row xs={1} md={2}>
          {content}
          </Row>
        </Container>
        </div>
         )
}
export default Products
