import { ADD_ADDRESS_FAILURE,ADD_ADDRESS_REQUEST,ADD_ADDRESS_SUCCESS,GET_ADDRESS,GET_ADDRESS_SUCCESS,GET_ADDRESS_FAILURE } from "./AddressType"
import axios from 'axios'
import { useDispatch, useSelector } from 'react-redux';
const user = useSelector((state) => state.auth.users._id);
export const addAddressRequest=()=>
{
    return{
        type:ADD_ADDRESS_REQUEST
    }
}
export const addAddressSuccess=address=>
{
    return{
        type:ADD_ADDRESS_SUCCESS,
        payload: address
    }
}
export const addAddressFailure=error=>
{
    return{
        type:ADD_ADDRESS_FAILURE,
        payload: error
    }
}
export const getAddressRequest=()=>
{
    return{
        type:GET_ADDRESS
    }
}
export const getAddressSuccess=address=>
{
    return{
        type:GET_ADDRESS_SUCCESS,
        payload: address
    }
}
export const getAddressFailure=error=>
{
    return{
        type:GET_ADDRESS_FAILURE,
        payload: error
    }
}
export const addAddress=addressDetails=>{
    const token = window.localStorage.getItem("token");
    const config = {
        headers: { Authorization: `Bearer ${token}` }
       
    };
    return(dispatch)=>{
        dispatch(addAddressRequest)
        axios.post('https://golocal999.herokuapp.com/api/address',addressDetails,config)
        .then(response =>{
            const address = response.data
            dispatch(addAddressSuccess(address))
        })
        .catch(error=>{
            const errorMsg =error.message
            dispatch(addAddressFailure(errorMsg))
        })
    }
}
export const getAddress=address=>{
    const token = window.localStorage.getItem("token");
    const config = {
        headers: { Authorization: `Bearer ${token}` }
       
    };
    return(dispatch)=>{
        dispatch(getAddressRequest())
        axios.get('https://golocal999.herokuapp.com/api/get-addresses/'+{user},config)
        .then(response =>{
            const products = response.data
            console.log(products)
            dispatch(getAddressSuccess(products))
        })
        .catch(error=>{
            const errorMsg =error.message
            dispatch(getAddressFailure(errorMsg))
        })
    }
}
