import React,{useState,useEffect} from 'react'
import axios from 'axios'
import Productcard from '../ProductsCard/ProductCard'
import {Container,Row,Col} from 'react-bootstrap'
function Products() {
    const url ='https://golocal999.herokuapp.com/api/getproduct'
    const [products,setProducts] =useState({
        loading:false,
        data:null,
        error:false
    })
    useEffect(() => {
        setProducts({
            loading:true,
            data:null,
            error:false
        })
    axios.get(url)
    .then(response=>{
        setProducts({
            loading:false,
            data:response.data.products,
            error:false
        })
    })
    .catch(()=>{
        setProducts({
            loading:false,
            data:null,
            error:true
        } 
    )
    })}, [url])
    let content =null
    if(products.error)
    {
        content=<p>Error fetching the data please check your internet connection</p>
    }
    if(products.loading)
    {
        content=<p>Loading Please wait ..........
        </p>
    }
    if(products.data)
    {
        content=products.data.map((product,key)=>
        <div key={key}>
        <Col><Productcard product={product}/></Col>
        
  </div>
        )
    }
    return (
        <div>
          <Container >
          <Row xs={1} md={2}>
          {content}
          </Row>
        </Container>
        </div>
        
    )
}
export default Products
